// Page for adding new item in the DB.
import React, {Component} from 'react'
import './ItemAdd.css';

export default class ItemAdd extends Component {
    constructor(props) {
        super(props);

        //this.sendCallback = this.sendCallback.bind(this);
        this.onNameChange = this.onNameChange.bind(this);
        this.onDescriptionChange = this.onDescriptionChange.bind(this);
        this.state = {
          name: "",
          description: ""
        }
    }

    onNameChange(event) {
      this.setState({ name: event.target.value });
    }

    onDescriptionChange(event) {
      this.setState({ description: event.target.value });
    }

    sendCallback = async() => {
      if (this.state.name === "" || this.state.description === "") {
        console.log("Inputs should not be empty")
      } else {
        console.log(this.state.name)
        console.log(this.state.description)
        await fetch('htpp://localhost:33336/admin/unos-novog-proizvoda', {
          method: 'POST',
          headers: {
            'Content-Type': 'application/json',
          },
          body: JSON.stringify({
            name: this.state.name,
            description: this.state.description
          })
        });
      }

      document.querySelectorAll('input').forEach(input => (input.value = ""));
      this.setState({name: undefined, description: undefined});
    }

    render() {
        return (
          <div className="create-entry">
            <h2>New entry</h2>
            <form className="ui form">
              <div className="field">
                <label>Product name:</label>
                <input
                  type="text"
                  onChange={this.onNameChange}
                  value={this.state.name}
                ></input>
              </div>
              <div className="field">
                <label>Product description:</label>
                <textarea
                  onChange={this.onDescriptionChange}
                  value = {this.state.description}
                ></textarea>
              </div>

              <button className="ui button" type="submit" onSubmit={this.sendCallback}>
                Create
              </button>
            </form>

            <p>{this.state.description}</p>
          </div>
        );
    }
}
